import React, { useState } from 'react';
import axios from 'axios';

function Login() {
  const [identifier, setIdentifier] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const response = await axios.post('http://localhost:8082/api/login', {
        identifier,
        password,
      });

      setMessage(response.data); // Expected to be a string like "Login successful"
    } catch (error) {
      if (error.response && error.response.data) {
        // Handle structured error response from Spring Boot
        setMessage(error.response.data.message || 'Login failed');
      } else {
        setMessage('Server error. Please try again.');
      }
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold text-green-800 mb-4">Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Email / Phone Number</label>
          <input
            type="text"
            value={identifier}
            onChange={(e) => setIdentifier(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded"
            required
          />
        </div>
        <button type="submit" className="w-full bg-green-700 text-white p-2 rounded hover:bg-green-800">
          Login
        </button>
      </form>

      {message && (
        <div className="mt-4 text-center text-sm text-red-600 font-semibold">
          {message}
        </div>
      )}
    </div>
  );
}

export default Login;
