import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Account = () => {
  const [orders, setOrders] = useState([]);
  const [summary, setSummary] = useState({});
  const [reports, setReports] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:8082/api/account', {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
    })
      .then((res) => res.json())
      .then((data) => {
        setOrders(data.orders || []);
        setSummary(data.summary || {});
        setReports(data.reports || []);
        setIsLoading(false);
      })
      .catch(() => {
        navigate('/login');
        setIsLoading(false);
      });
  }, [navigate]);

  const handlePayment = () => {
    navigate('/payment');
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-bold text-ewaste-green mb-6">Account Dashboard</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-4 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold text-ewaste-green mb-2">Summary</h3>
          <p>Total Orders: {summary.totalOrders || 0}</p>
          <p>Total Paid: ${summary.totalPaid || 0}</p>
        </div>
        <div className="bg-white p-4 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold text-ewaste-green mb-2">Previous Orders</h3>
          <ul className="list-disc pl-5">
            {orders.map((order) => (
              <li key={order.id} className="text-gray-700">{order.details}</li>
            ))}
          </ul>
        </div>
        <div className="bg-white p-4 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold text-ewaste-green mb-2">Reports</h3>
          <ul className="list-disc pl-5">
            {reports.map((report) => (
              <li key={report.id} className="text-gray-700">{report.details}</li>
            ))}
          </ul>
        </div>
      </div>
      <button
        onClick={handlePayment}
        className="mt-6 bg-ewaste-green text-white py-2 px-4 rounded hover:bg-ewaste-light-green transition duration-200"
      >
        Schedule Pickup & Pay
      </button>
    </div>
  );
};

export default Account;
