import React, { useEffect, useState } from 'react';

function Profile() {
  const [user, setUser] = useState({
    id: '',
    fullname: '',
    email: '',
    phone: '',
    location: '',
    role: '',
    password: ''
  });

  const wasteItems = [
    { name: 'Fridge', price: '‚Çπ1,500', image: 'https://images.jdmagicbox.com/quickquotes/images_main/refrigerator-scrap-2219509427-iw6dq6oa.jpg' },
    { name: 'TV', price: '‚Çπ800', image: 'https://i.pinimg.com/736x/9d/c0/7e/9dc07e2422dd353ca0547d877500635b.jpg' },
    { name: 'Cooler', price: '‚Çπ500', image: 'https://5.imimg.com/data5/SELLER/Default/2022/2/KV/DD/HM/146664232/cooler-waste-scrap-500x500.png' },
    { name: 'Washing Machine', price: '‚Çπ1,200', image: 'https://as1.ftcdn.net/jpg/02/80/56/26/1000_F_280562639_qYris0M1bcJHBSjTmrtt1ueHLaDCw3Bd.jpg' },
    { name: 'Microwave', price: '‚Çπ700', image: 'https://c8.alamy.com/comp/2KYKPA1/old-broken-small-hot-plate-oven-and-other-old-e-waste-appliances-pilled-up-in-the-background-2KYKPA1.jpg' }
  ];

  const [pickups, setPickups] = useState([]);
  const [view, setView] = useState('dashboard'); // 'dashboard' | 'edit' | 'pickup'

  useEffect(() => {
    const userId = localStorage.getItem('userId');

    const fetchProfile = async () => {
      try {
        const response = await fetch(`http://localhost:8082/api/user/${userId}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    };

    const fetchPickups = async () => {
      try {
        const response = await fetch(`http://localhost:8082/api/pickups/${userId}`);
        const data = await response.json();
        setPickups(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error('Error fetching pickups:', error);
      }
    };

    fetchProfile();
    fetchPickups();
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    try {
      const { id, fullname, location, password } = user;
      const updateData = { id, fullname, location, password };

      const response = await fetch(`http://localhost:8082/api/user/update`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updateData),
      });

      if (response.ok) {
        alert('Profile updated successfully!');
        setView('dashboard');
      } else {
        alert('Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div className="flex bg-gray-50 min-h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white min-h-screen p-4 space-y-4">
        <h1 className="text-xl font-bold mb-4">E-Waste Manager</h1>
        <ul className="space-y-2">
          <li
            className={`cursor-pointer ${view === 'dashboard' ? 'text-white font-semibold' : 'text-gray-300 hover:text-white'}`}
            onClick={() => setView('dashboard')}
          >
            Dashboard
          </li>
          <li
            className={`cursor-pointer ${view === 'edit' ? 'text-white font-semibold' : 'text-gray-300 hover:text-white'}`}
            onClick={() => setView('edit')}
          >
            Edit Profile
          </li>
          <li
            className={`cursor-pointer ${view === 'pickup' ? 'text-white font-semibold' : 'text-gray-300 hover:text-white'}`}
            onClick={() => setView('pickup')}
          >
            Pickups
          </li>
        </ul>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-10">
        {/* Profile Info Card */}
        {view === 'dashboard' && (
          <div className="bg-white rounded-lg shadow-md p-6 text-center max-w-xl mx-auto mb-8">
            <img
              src="https://i.pravatar.cc/150?img=3"
              alt="Profile"
              className="w-24 h-24 rounded-full mx-auto mb-4"
            />
            <h3 className="text-lg font-bold">{user.fullname || 'User Name'}</h3>
            <p className="text-sm text-gray-500">{user.role || 'User'}</p>
            <p className="text-gray-600 mt-1">{user.location || 'N/A'}</p>
          </div>
        )}

        {/* Edit Profile Form */}
        {view === 'edit' && (
          <div className="bg-white p-6 rounded-lg shadow-md max-w-xl mx-auto mb-10">
            <h2 className="text-2xl font-bold text-green-800 mb-6">Edit Profile</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-gray-700">Full Name</label>
                <input
                  type="text"
                  name="fullname"
                  value={user.fullname}
                  onChange={handleChange}
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>
              <div>
                <label className="block text-gray-700">Location</label>
                <input
                  type="text"
                  name="location"
                  value={user.location}
                  onChange={handleChange}
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>
              <div>
                <label className="block text-gray-700">New Password</label>
                <input
                  type="password"
                  name="password"
                  value={user.password || ''}
                  onChange={handleChange}
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>
              <div className="text-right">
                <button
                  onClick={handleUpdate}
                  className="bg-green-700 text-white px-4 py-2 rounded hover:bg-green-800"
                >
                  Save Changes
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Pickup History Section */}
        {view === 'pickup' && (
          <section className="mt-10">
            <h2 className="text-xl font-bold text-green-800 mb-4">Past Pickup Details</h2>
            {pickups.length > 0 ? (
              <div className="overflow-auto">
                <table className="w-full table-auto border">
                  <thead>
                    <tr className="bg-green-100 text-left">
                      <th className="p-2 border">Date</th>
                      <th className="p-2 border">Item Type</th>
                      <th className="p-2 border">Weight (kg)</th>
                      <th className="p-2 border">Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {pickups.map((pickup, index) => (
                      <tr key={index}>
                        <td className="p-2 border">{pickup.date || 'N/A'}</td>
                        <td className="p-2 border">{pickup.itemType || 'N/A'}</td>
                        <td className="p-2 border">{pickup.weight || 'N/A'}</td>
                        <td className="p-2 border">{pickup.status || 'N/A'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="text-gray-500">No pickup history available.</p>
            )}
          </section>
        )}

        {/* Smart Insights */}
        {view === 'dashboard' && (
  <section className="mt-10">
    <h2 className="text-xl font-bold text-green-800 mb-4">Smart Insights</h2>
    
    <ul className="list-disc list-inside text-gray-700 space-y-2 mb-6">
      <li>
        ‚ôªÔ∏è You've recycled{' '}
        <strong>
          {pickups.reduce((sum, p) => sum + (p.weight || 0), 0)}
        </strong>{' '}
        kg of e-waste!
      </li>
      <li>üìÖ Suggested next pickup: <strong>Coming soon</strong></li>
      <li>üìä Impact charts & insights (coming soon)</li>
    </ul>

    {/* Waste Item Prices */}
    <h3 className="text-lg font-semibold text-green-700 mb-3">Estimated Value of Common E-Waste Items</h3>
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 mb-6">
  {wasteItems.map((item, idx) => (
    <div key={idx} className="bg-white p-4 rounded-lg shadow text-center">
      <img
        src={item.image}
        alt={item.name}
        className="mx-auto mb-4 object-cover rounded"
        style={{ width: '200px', height: '250px' }} // Larger size
      />
      <p className="font-bold text-lg">{item.name}</p>
      <p className="text-green-600 text-md">Approx. ‚Çπ{item.price}</p>
    </div>
  ))}
</div>

    {/* Terms and Conditions */}
    <div className="bg-gray-100 p-4 rounded-md text-sm text-gray-700">
      <h4 className="font-bold mb-2">Terms & Conditions:</h4>
      <ul className="list-disc list-inside space-y-1">
        <li>Prices shown are approximate and may vary based on item condition and location.</li>
        <li>Items must be in a recyclable state (no excessive damage or contamination).</li>
        <li>Pickups are subject to availability and area coverage.</li>
        <li>We reserve the right to reject items that don‚Äôt meet minimum requirements.</li>
      </ul>
    </div>
  </section>
)}
      </main>
    </div>
  );
}

export default Profile;
