import React, { useState, useEffect, useRef } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '80vh',
};

const defaultCenter = {
  lat: 20.5937,
  lng: 78.9629,
};

function Maps() {
  const [currentPosition, setCurrentPosition] = useState(null);
  const [nearbyCenters, setNearbyCenters] = useState([]);
  const [selected, setSelected] = useState(null);
  const [loading, setLoading] = useState(true);
  const [searchComplete, setSearchComplete] = useState(false);
  const mapRef = useRef(null);

  // Improved live tracking with watchPosition
  useEffect(() => {
    const watchId = navigator.geolocation.watchPosition(
      (position) => {
        const pos = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        };
        setCurrentPosition(pos);
        setLoading(false);
        console.log('Accuracy (meters):', position.coords.accuracy);
      },
      (err) => {
        console.error('Geolocation error:', err);
        setCurrentPosition(defaultCenter);
        setLoading(false);
      },
      {
        enableHighAccuracy: true,
        maximumAge: 0,
        timeout: 10000,
      }
    );

    return () => navigator.geolocation.clearWatch(watchId);
  }, []);

  // Load nearby places when map is ready
  const onMapLoad = (map) => {
    mapRef.current = map;

    if (!currentPosition) return;

    const service = new window.google.maps.places.PlacesService(map);

    const request = {
      location: currentPosition,
      radius: 10000, // Increased to 10km
      type: ['point_of_interest'],
      keyword: 'kabadiwala scrap e-waste recycling center',
    };

    service.nearbySearch(request, (results, status) => {
      setSearchComplete(true);
      if (status === window.google.maps.places.PlacesServiceStatus.OK) {
        setNearbyCenters(results);
      } else {
        console.error('PlacesService error:', status);
      }
    });

    map.panTo(currentPosition);
    map.setZoom(14);
  };

  const handleRecenter = () => {
    if (mapRef.current && currentPosition) {
      mapRef.current.panTo(currentPosition);
      mapRef.current.setZoom(14);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-2">Nearest E-Waste Collection Centers</h2>

      {loading && <p>Getting your location...</p>}

      {!loading && currentPosition && (
        <>
          <div className="flex justify-between items-center mb-2">
            <p className="text-sm text-gray-600">
              Your Location: {currentPosition.lat.toFixed(5)}, {currentPosition.lng.toFixed(5)}
            </p>
            <button
              onClick={handleRecenter}
              className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 text-sm"
            >
              Recenter
            </button>
          </div>

          <LoadScript
            googleMapsApiKey="AIzaSyAH8Mqz2Jazg0Q0tM_XA27CSgzRqS7v2Kk"
            libraries={['places']}
            loading="async"
          >
            <GoogleMap
              mapContainerStyle={containerStyle}
              center={currentPosition}
              zoom={14}
              onLoad={onMapLoad}
            >
              {/* User Marker */}
              <Marker
                position={currentPosition}
                icon={{ url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png' }}
              />

              {/* Nearby Centers */}
              {nearbyCenters.map((place) => (
                <Marker
                  key={place.place_id}
                  position={place.geometry.location}
                  onClick={() => setSelected(place)}
                />
              ))}

              {/* InfoWindow */}
              {selected && (
                <InfoWindow
                  position={selected.geometry.location}
                  onCloseClick={() => setSelected(null)}
                >
                  <div>
                    <h3 className="font-semibold">{selected.name}</h3>
                    <p>{selected.vicinity}</p>
                    {selected.rating && <p>Rating: {selected.rating}</p>}
                  </div>
                </InfoWindow>
              )}
            </GoogleMap>
          </LoadScript>

          {/* No Results Fallback */}
          {searchComplete && nearbyCenters.length === 0 && (
            <div className="mt-4 text-red-600 font-medium">
              No nearby e-waste centers or kabadiwalas found within 10km.
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default Maps;
