import React, { useState, useEffect, useRef } from 'react';
import {
  GoogleMap,
  useJsApiLoader,
  Marker,
  InfoWindow,
} from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '100%',
};

const defaultCenter = {
  lat: 20.5937,
  lng: 78.9629,
};

const Maps = () => {
  const [currentPosition, setCurrentPosition] = useState(null);
  const [nearbyCenters, setNearbyCenters] = useState([]);
  const [selected, setSelected] = useState(null);
  const [mapRef, setMapRef] = useState(null);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyAH8Mqz2Jazg0Q0tM_XA27CSgzRqS7v2Kk',
    libraries: ['places'],
  });

  // Watch position to keep updating on movement
  useEffect(() => {
    const watchId = navigator.geolocation.watchPosition(
      (position) => {
        const pos = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        };
        setCurrentPosition(pos);
      },
      (err) => {
        console.error('Geolocation error:', err);
        setCurrentPosition(defaultCenter);
      },
      { enableHighAccuracy: true }
    );
    return () => navigator.geolocation.clearWatch(watchId);
  }, []);

  // Load nearby kabadiwalas whenever location changes
  useEffect(() => {
    if (!mapRef || !currentPosition) return;

    const service = new window.google.maps.places.PlacesService(mapRef);

    const request = {
      location: currentPosition,
      radius: 5000,
      type: ['store'],
      keyword: 'kabadiwala scrap e-waste',
    };

    service.nearbySearch(request, (results, status) => {
      if (status === window.google.maps.places.PlacesServiceStatus.OK) {
        setNearbyCenters(results);
        if (mapRef) {
          mapRef.panTo(currentPosition);
          mapRef.setZoom(14);
        }
      } else {
        console.error('Nearby search failed:', status);
      }
    });
  }, [currentPosition, mapRef]);

  return isLoaded && currentPosition ? (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar */}
      <div
        style={{
          width: '30%',
          overflowY: 'auto',
          padding: '1rem',
          borderRight: '1px solid #ccc',
          backgroundColor: '#f9f9f9',
        }}
      >
        <h2>Nearby Kabadiwalas</h2>
        {nearbyCenters.length === 0 ? (
          <p>Searching for nearby centers...</p>
        ) : (
          nearbyCenters.map((place) => (
            <div
              key={place.place_id}
              style={{
                padding: '10px',
                marginBottom: '10px',
                border: '1px solid #ddd',
                borderRadius: '8px',
                backgroundColor: selected?.place_id === place.place_id ? '#e6f7ff' : '#fff',
              }}
            >
              <h4>{place.name}</h4>
              <p>{place.vicinity}</p>
              {place.rating && (
                <p>
                  ⭐ {place.rating} ({place.user_ratings_total})
                </p>
              )}
              <button
                onClick={() => {
                  setSelected(place);
                  if (mapRef) mapRef.panTo(place.geometry.location);
                }}
                style={{
                  marginTop: '5px',
                  padding: '5px 10px',
                  backgroundColor: '#007bff',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                }}
              >
                View on Map
              </button>
            </div>
          ))
        )}
      </div>

      {/* Map Section */}
      <div style={{ width: '70%', height: '100%' }}>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={currentPosition}
          zoom={14}
          onLoad={(map) => setMapRef(map)}
        >
          <Marker
            position={currentPosition}
            icon={{ url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png' }}
          />

          {nearbyCenters.map((place) => (
            <Marker
              key={place.place_id}
              position={place.geometry.location}
              onClick={() => setSelected(place)}
            />
          ))}

          {selected && (
            <InfoWindow
              position={selected.geometry.location}
              onCloseClick={() => setSelected(null)}
            >
              <div>
                <h3>{selected.name}</h3>
                <p>{selected.vicinity}</p>
                {selected.rating && <p>Rating: {selected.rating}</p>}
              </div>
            </InfoWindow>
          )}
        </GoogleMap>
      </div>
    </div>
  ) : (
    <p>Loading map and location...</p>
  );
};

export default Maps;
