import React, { useEffect, useState } from 'react';

function Profile() {
  const [user, setUser] = useState({
    fullname: '',
    email: '',
    phone: '',
    location: '',
    role: '',
  });

  const [pickups, setPickups] = useState([]);
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    const userId = localStorage.getItem('userId');

    const fetchProfile = async () => {
      try {
        const response = await fetch(`http://localhost:8082/api/user/${userId}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    };

    const fetchPickups = async () => {
      try {
        const response = await fetch(`http://localhost:8082/api/pickups/${userId}`);
        const data = await response.json();
        setPickups(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error('Error fetching pickups:', error);
      }
    };

    fetchProfile();
    fetchPickups();
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    try {
      const response = await fetch(`http://localhost:8082/api/user/update`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(user),
      });

      if (response.ok) {
        alert('Profile updated successfully!');
        setEditMode(false);
      } else {
        alert('Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div className="flex bg-gray-50 min-h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white min-h-screen p-4 space-y-4">
        <h1 className="text-xl font-bold mb-4">E-Waste Manager</h1>
        <ul className="space-y-2">
          <li className="text-gray-300 hover:text-white cursor-pointer">Dashboard</li>
          <li className="text-white font-semibold">Settings</li>
          <li className="text-gray-300 hover:text-white cursor-pointer">Pickups</li>
        </ul>
      </aside>

      {/* Profile Content */}
      <main className="flex-1 p-10">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">
          {/* Form Section */}
          <div className="lg:col-span-2 bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-bold text-green-800 mb-6">General Information</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-gray-700">Full Name</label>
                <input
                  type="text"
                  name="fullname"
                  value={user.fullname}
                  onChange={handleChange}
                  readOnly={!editMode}
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>

              <div>
                <label className="block text-gray-700">Phone</label>
                <input
                  type="text"
                  name="phone"
                  value={user.phone}
                  onChange={handleChange}
                  readOnly={!editMode}
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>

              <div>
                <label className="block text-gray-700">Email</label>
                <input
                  type="email"
                  value={user.email}
                  readOnly
                  className="w-full p-2 border border-gray-300 rounded bg-gray-100"
                />
              </div>

              <div>
                <label className="block text-gray-700">Location</label>
                <input
                  type="text"
                  name="location"
                  value={user.location}
                  onChange={handleChange}
                  readOnly={!editMode}
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>
            </div>

            <div className="mt-6 text-right">
              {editMode ? (
                <button onClick={handleUpdate} className="bg-green-700 text-white px-4 py-2 rounded hover:bg-green-800">
                  Save Changes
                </button>
              ) : (
                <button onClick={() => setEditMode(true)} className="bg-green-700 text-white px-4 py-2 rounded hover:bg-green-800">
                  Edit Profile
                </button>
              )}
            </div>
          </div>

          {/* Profile Card */}
          <div className="bg-white rounded-lg shadow-md p-6 text-center">
            <img
              src="https://i.pravatar.cc/150?img=3"
              alt="Profile"
              className="w-24 h-24 rounded-full mx-auto mb-4"
            />
            <h3 className="text-lg font-bold">{user.fullname || 'User Name'}</h3>
            <p className="text-sm text-gray-500">{user.role || 'User'}</p>
            <p className="text-gray-600 mt-1">{user.location || 'N/A'}</p>
          </div>
        </div>

        {/* Pickup History Section */}
        <section className="mt-10">
          <h2 className="text-xl font-bold text-green-800 mb-4">Past Pickup Details</h2>
          {pickups.length > 0 ? (
            <div className="overflow-auto">
              <table className="w-full table-auto border">
                <thead>
                  <tr className="bg-green-100 text-left">
                    <th className="p-2 border">Date</th>
                    <th className="p-2 border">Item Type</th>
                    <th className="p-2 border">Weight (kg)</th>
                    <th className="p-2 border">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {pickups.map((pickup, index) => (
                    <tr key={index}>
                      <td className="p-2 border">{pickup.date || 'N/A'}</td>
                      <td className="p-2 border">{pickup.itemType || 'N/A'}</td>
                      <td className="p-2 border">{pickup.weight || 'N/A'}</td>
                      <td className="p-2 border">{pickup.status || 'N/A'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-500">No pickup history available.</p>
          )}
        </section>

        {/* Smart Insights */}
        <section className="mt-10">
          <h2 className="text-xl font-bold text-green-800 mb-4">Smart Insights</h2>
          <ul className="list-disc list-inside text-gray-700 space-y-2">
            <li>
              ♻️ You've recycled{' '}
              <strong>
                {pickups.reduce((sum, p) => sum + (p.weight || 0), 0)}
              </strong>{' '}
              kg of e-waste!
            </li>
            <li>📅 Suggested next pickup: <strong>Coming soon</strong></li>
            <li>📊 Impact charts & insights (coming soon)</li>
          </ul>
        </section>
      </main>
    </div>
  );
}

export default Profile;
