import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';

const MapsLeaflet = () => {
  const [position, setPosition] = useState(null);
  const [places, setPlaces] = useState([]);

  const API_KEY = 'AIzaSyAH8Mqz2Jazg0Q0tM_XA27CSgzRqS7v2Kk'; // Replace with your Google Maps API key

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      async (pos) => {
        const coords = {
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        };
        setPosition(coords);

        const radius = 10000; // 10 km in meters
        const type = 'kabadi wale'; // or try: 'electronics_store'

        try {
          const response = await axios.get(
            `https://maps.googleapis.com/maps/api/place/nearbysearch/json`,
            {
              params: {
                location: `${coords.lat},${coords.lng}`,
                radius: radius,
                keyword: 'e-waste recycling',
                key: API_KEY,
              },
            }
          );

          setPlaces(response.data.results);
        } catch (error) {
          console.error('Places API Error:', error);
        }
      },
      (err) => {
        console.error('Geolocation error:', err);
        alert('Please allow location access for this feature to work.');
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 0,
      }
    );
  }, []);

  const userIcon = new L.Icon({
    iconUrl: 'https://maps.google.com/mapfiles/ms/icons/blue-dot.png',
    iconSize: [32, 32],
    iconAnchor: [16, 32],
  });

  return position ? (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar */}
      <div style={{ width: '30%', padding: '1rem', overflowY: 'auto', background: '#f9f9f9' }}>
        <h3>E-Waste Centers Nearby</h3>
        {places.length === 0 ? (
          <p>No centers found nearby.</p>
        ) : (
          places.map((place, idx) => (
            <div key={idx} style={{ marginBottom: '12px', padding: '10px', background: '#fff', borderRadius: '5px' }}>
              <h4>{place.name}</h4>
              <p style={{ fontSize: '14px' }}>{place.vicinity}</p>
            </div>
          ))
        )}
      </div>

      {/* Map */}
      <div style={{ width: '70%' }}>
        <MapContainer center={position} zoom={13} style={{ height: '100vh', width: '100%' }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />

          <Marker position={position} icon={userIcon}>
            <Popup>You are here</Popup>
          </Marker>

          {places.map((place, index) => (
            <Marker
              key={index}
              position={{
                lat: place.geometry.location.lat,
                lng: place.geometry.location.lng,
              }}
            >
              <Popup>
                <strong>{place.name}</strong><br />
                {place.vicinity}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>
    </div>
  ) : (
    <p style={{ padding: '2rem' }}>Getting your location...</p>
  );
};

export default MapsLeaflet;
