import React, { useState, useEffect, useRef } from 'react';
import {
  GoogleMap,
  useJsApiLoader,
  Marker,
  InfoWindow,
} from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '100%',
};

const defaultCenter = {
  lat: 20.5937,
  lng: 78.9629,
};

const Maps = () => {
  const [currentPosition, setCurrentPosition] = useState(null);
  const [nearbyCenters, setNearbyCenters] = useState([]);
  const [selected, setSelected] = useState(null);
  const [searchComplete, setSearchComplete] = useState(false);
  const mapRef = useRef(null);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyAH8Mqz2Jazg0Q0tM_XA27CSgzRqS7v2Kk',
    libraries: ['places'],
  });

  // Get accurate user location
  useEffect(() => {
    const watchId = navigator.geolocation.watchPosition(
      (position) => {
        const pos = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        };
        setCurrentPosition(pos);
      },
      (err) => {
        console.error('Geolocation error:', err);
        setCurrentPosition(defaultCenter);
      },
      { enableHighAccuracy: true }
    );

    return () => navigator.geolocation.clearWatch(watchId);
  }, []);

  // Load nearby kabadiwala/scrap centers
  const onMapLoad = (map) => {
    mapRef.current = map;

    if (!currentPosition) return;

    const service = new window.google.maps.places.PlacesService(map);

    const request = {
      location: currentPosition,
      radius: 5000,
      type: ['store'],
      keyword: 'kabadiwala scrap e-waste',
    };

    service.nearbySearch(request, (results, status) => {
      setSearchComplete(true);
      if (status === window.google.maps.places.PlacesServiceStatus.OK) {
        setNearbyCenters(results);
      } else {
        console.error('PlacesService error:', status);
      }
    });

    map.panTo(currentPosition);
    map.setZoom(14);
  };

  return isLoaded && currentPosition ? (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar List */}
      <div
        style={{
          width: '30%',
          overflowY: 'auto',
          padding: '1rem',
          borderRight: '1px solid #ddd',
        }}
      >
        <h2 style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '1rem' }}>
          Nearby Kabadiwalas
        </h2>

        {searchComplete && nearbyCenters.length === 0 && (
          <p>No centers found nearby.</p>
        )}

        {nearbyCenters.map((place, idx) => (
          <div
            key={place.place_id}
            style={{
              border: '1px solid #ccc',
              padding: '0.75rem',
              marginBottom: '0.75rem',
              borderRadius: '8px',
              backgroundColor: selected?.place_id === place.place_id ? '#f0f8ff' : '#fff',
            }}
          >
            <h3 style={{ fontSize: '1rem', fontWeight: 'bold' }}>{place.name}</h3>
            <p style={{ fontSize: '0.9rem', margin: '0.25rem 0' }}>
              {place.vicinity}
            </p>
            {place.rating && (
              <p style={{ fontSize: '0.85rem', color: '#777' }}>
                ⭐ {place.rating} ({place.user_ratings_total})
              </p>
            )}
            <button
              onClick={() => {
                setSelected(place);
                mapRef.current.panTo(place.geometry.location);
              }}
              style={{
                marginTop: '0.5rem',
                padding: '5px 10px',
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              View on Map
            </button>
          </div>
        ))}
      </div>

      {/* Google Map */}
      <div style={{ width: '70%', height: '100%' }}>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={currentPosition}
          zoom={14}
          onLoad={onMapLoad}
        >
          {/* User Location Marker */}
          <Marker
            position={currentPosition}
            icon={{ url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png' }}
          />

          {/* Kabadiwala Markers */}
          {nearbyCenters.map((place) => (
            <Marker
              key={place.place_id}
              position={place.geometry.location}
              onClick={() => setSelected(place)}
            />
          ))}

          {/* Info Window on Marker Click */}
          {selected && (
            <InfoWindow
              position={selected.geometry.location}
              onCloseClick={() => setSelected(null)}
            >
              <div>
                <h3>{selected.name}</h3>
                <p>{selected.vicinity}</p>
                {selected.rating && <p>Rating: {selected.rating}</p>}
              </div>
            </InfoWindow>
          )}
        </GoogleMap>
      </div>
    </div>
  ) : (
    <p>Loading map and your location...</p>
  );
};

export default Maps;
