import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '80vh',
};

const center = {
  lat: 20.5937, // Default center of India
  lng: 78.9629,
};

function Maps() {
  const [currentPosition, setCurrentPosition] = useState(null);
  const [selected, setSelected] = useState(null);

  const eWasteCenters = [
    {
      id: 1,
      name: "Green Recycle Hub",
      position: { lat: 28.6448, lng: 77.216721 },
    },
    {
      id: 2,
      name: "EcoDrop E-Waste Center",
      position: { lat: 19.0760, lng: 72.8777 },
    },
    {
      id: 3,
      name: "ReEarth Waste Point",
      position: { lat: 12.9716, lng: 77.5946 },
    },
  ];

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setCurrentPosition({
        lat: position.coords.latitude,
        lng: position.coords.longitude,
      });
    });
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Nearest E-Waste Centers</h2>
      <LoadScript googleMapsApiKey="AIzaSyAH8Mqz2Jazg0Q0tM_XA27CSgzRqS7v2Kk">
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={currentPosition || center}
          zoom={currentPosition ? 12 : 5}
        >
          {/* User Location Marker */}
          {currentPosition && (
            <Marker
              position={currentPosition}
              icon={{
                url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png',
              }}
            />
          )}

          {/* E-Waste Center Markers */}
          {eWasteCenters.map((center) => (
            <Marker
              key={center.id}
              position={center.position}
              onClick={() => setSelected(center)}
            />
          ))}

          {/* InfoWindow */}
          {selected && (
            <InfoWindow
              position={selected.position}
              onCloseClick={() => setSelected(null)}
            >
              <div>
                <h3 className="font-semibold">{selected.name}</h3>
                <p>Open 9am - 6pm</p>
              </div>
            </InfoWindow>
          )}
        </GoogleMap>
      </LoadScript>
    </div>
  );
}

export default Maps;
