import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [phone, setPhone] = useState('');
  const [location, setLocation] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!location) {
      setError('Please allow location access to proceed.');
      return;
    }
    try {
      const response = await fetch('http://localhost:8082/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, location: location.coords }),
      });
      if (response.ok) {
        navigate('/account');
      } else {
        setError('Invalid phone number or location not allowed.');
      }
    } catch (err) {
      setError('Something went wrong. Please try again.');
    }
  };

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setLocation(pos),
        (err) => setError('Location access denied.')
      );
    } else {
      setError('Geolocation is not supported by this browser.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-ewaste-light-green to-ewaste-green flex items-center justify-center">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-ewaste-green text-center mb-4">Login</h2>
        {error && <p className="text-red-500 text-center mb-4">{error}</p>}
        <button
          onClick={getLocation}
          className="w-full bg-ewaste-green text-white py-2 rounded hover:bg-ewaste-light-green transition mb-4"
        >
          Allow Location Access
        </button>
        <form onSubmit={handleLogin} className="space-y-4">
          <div>
            <label className="block text-gray-700">Phone Number</label>
            <input
              type="tel"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              className="w-full p-2 border rounded"
              required
              disabled={!location}
            />
          </div>
          <button
            type="submit"
            className="w-full bg-ewaste-green text-white py-2 rounded hover:bg-ewaste-light-green transition disabled:bg-gray-400"
            disabled={!location}
          >
            Login
          </button>
        </form>
        <p className="mt-4 text-center">
          Don't have an account? <Link to="/register" className="text-ewaste-green hover:underline">Register</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
