import React, { useState } from 'react';

const Payment = () => {
  const [amount] = useState(100); // Example amount for pickup
  const [isProcessing, setIsProcessing] = useState(false);

  const handlePayment = () => {
    setIsProcessing(true);

    const options = {
      key: 'rzp_test_bx3dOuw8U5u', // Replace with your Razorpay key
      amount: amount * 100, // Amount in paise
      currency: 'INR',
      name: 'E-Waste Management',
      description: 'Payment for E-Waste Pickup',
      handler: function (response) {
        alert('Payment successful! Payment ID: ' + response.razorpay_payment_id);
        setIsProcessing(false); // Reset after payment is completed
      },
      prefill: {
        name: 'Customer Name',
        email: 'customer@example.com',
        contact: '9999999999',
      },
      notes: {
        address: 'Customer Address',
      },
      theme: {
        color: '#2e7d32',
      },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-bold text-ewaste-green mb-4">Payment for Pickup</h2>
      <p className="text-lg text-gray-700 mb-4">Amount: â‚¹{amount}</p>
      <button
        onClick={handlePayment}
        className={`bg-ewaste-green text-white py-2 px-4 rounded hover:bg-ewaste-light-green transition ${isProcessing ? 'cursor-not-allowed bg-gray-400' : ''}`}
        disabled={isProcessing}
      >
        {isProcessing ? 'Processing...' : 'Pay Now'}
      </button>
    </div>
  );
};

export default Payment;
