import React, { useEffect, useState, useRef } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '80vh',
};

const defaultCenter = {
  lat: 20.5937,
  lng: 78.9629,
};

const Maps = () => {
  const [currentPosition, setCurrentPosition] = useState(null);
  const [nearbyCenters, setNearbyCenters] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const mapRef = useRef(null);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      const userLoc = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
      };
      setCurrentPosition(userLoc);
    });
  }, []);

  const handleLoad = (map) => {
    mapRef.current = map;
    if (currentPosition) {
      searchNearby(map, currentPosition);
    }
  };

  const searchNearby = (map, location) => {
    const service = new window.google.maps.places.PlacesService(map);

    const request = {
      location,
      radius: 10000, // 10 km
      keyword: 'kabadiwala OR e-waste OR scrap collector OR recycling center',
    };

    service.nearbySearch(request, (results, status) => {
      if (status === window.google.maps.places.PlacesServiceStatus.OK) {
        const places = results.map((place) => ({
          id: place.place_id,
          name: place.name,
          position: place.geometry.location,
          vicinity: place.vicinity,
        }));
        setNearbyCenters(places);
      }
    });
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-2">Nearest E-Waste Collection Centers</h2>
      {currentPosition && (
        <p className="mb-4">
          <strong>Your Location:</strong> {currentPosition.lat.toFixed(5)}, {currentPosition.lng.toFixed(5)}
        </p>
      )}

      <LoadScript
        googleMapsApiKey="YOUR_API_KEY_HERE"
        libraries={['places']}
      >
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={currentPosition || defaultCenter}
          zoom={currentPosition ? 13 : 5}
          onLoad={handleLoad}
        >
          {/* Marker for user's current position */}
          {currentPosition && (
            <Marker
              position={currentPosition}
              icon={{ url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png' }}
            />
          )}

          {/* Markers for nearby centers */}
          {nearbyCenters.map((place) => (
            <Marker
              key={place.id}
              position={place.position}
              onClick={() => setSelectedPlace(place)}
            />
          ))}

          {/* InfoWindow for selected place */}
          {selectedPlace && (
            <InfoWindow
              position={selectedPlace.position}
              onCloseClick={() => setSelectedPlace(null)}
            >
              <div>
                <h3 className="font-semibold">{selectedPlace.name}</h3>
                <p>{selectedPlace.vicinity}</p>
              </div>
            </InfoWindow>
          )}
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default Maps;
