import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Pickup() {
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  const [pickupData, setPickupData] = useState({
    date: '',
    time: '',
    address: '',
    pincode: '',
    city: '',
    state: '',
    schedulerName: '',
    phone: '',
    email: '',
    estimatedWeight: '',
  });

  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setPickupData({ ...pickupData, [e.target.name]: e.target.value });
  };

  const loadRazorpay = (callback) => {
    const script = document.createElement('script');
    script.src = 'https://checkout.razorpay.com/v1/checkout.js';
    script.onload = callback;
    script.onerror = () => alert('Failed to load Razorpay. Try again later.');
    document.body.appendChild(script);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    loadRazorpay(() => {
      const options = {
        key: 'rzp_test_1234567890abcdef', // Replace with your Razorpay key
        amount: 4000, // â‚¹40 in paise
        currency: 'INR',
        name: 'E-Waste Pickup',
        description: 'Pickup Service Charge',
        handler: async function (response) {
          // On successful payment
          try {
            const payload = { ...pickupData, userId };
            const res = await fetch('http://localhost:8082/api/pickup/schedule', {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify(payload),
            });

            if (res.ok) {
              alert('Pickup Scheduled Successfully!');
              navigate('/profile');
            } else {
              alert('Failed to schedule pickup');
            }
          } catch (error) {
            console.error('Pickup error:', error);
            alert('Error occurred while scheduling pickup.');
          } finally {
            setLoading(false);
          }
        },
        prefill: {
          name: pickupData.schedulerName,
          email: pickupData.email,
          contact: pickupData.phone,
        },
        theme: {
          color: '#00aa55',
        },
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
      setLoading(false);
    });
  };

  return (
    <div className="min-h-screen bg-green-50 flex items-center justify-center px-4">
      <div className="max-w-2xl w-full bg-white p-8 rounded shadow">
        <h2 className="text-2xl font-bold text-green-800 mb-6 text-center">ðŸ“¦ Schedule a Pickup</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input type="date" name="date" value={pickupData.date} onChange={handleChange} className="p-3 border rounded" required />
            <input type="time" name="time" value={pickupData.time} onChange={handleChange} className="p-3 border rounded" required />
            <input type="text" name="address" value={pickupData.address} onChange={handleChange} placeholder="Full Address" className="p-3 border rounded" required />
            <input type="text" name="pincode" value={pickupData.pincode} onChange={handleChange} placeholder="Pincode" className="p-3 border rounded" required />
            <input type="text" name="city" value={pickupData.city} onChange={handleChange} placeholder="City" className="p-3 border rounded" required />
            <input type="text" name="state" value={pickupData.state} onChange={handleChange} placeholder="State" className="p-3 border rounded" required />
            <input type="text" name="schedulerName" value={pickupData.schedulerName} onChange={handleChange} placeholder="Your Name" className="p-3 border rounded" required />
            <input type="tel" name="phone" value={pickupData.phone} onChange={handleChange} placeholder="Phone Number" className="p-3 border rounded" required />
            <input type="email" name="email" value={pickupData.email} onChange={handleChange} placeholder="Email Address" className="p-3 border rounded" required />
            <input type="number" name="estimatedWeight" value={pickupData.estimatedWeight} onChange={handleChange} placeholder="Estimated Weight (kg)" className="p-3 border rounded" required />
          </div>
          <div className="text-center mt-6">
            <button type="submit" disabled={loading} className="bg-green-700 hover:bg-green-800 text-white font-semibold px-6 py-3 rounded">
              {loading ? 'Scheduling...' : 'Pay â‚¹40 & Schedule Pickup'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Pickup;
