import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Profile() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [pickupHistory, setPickupHistory] = useState([]);
  const [totalWeight, setTotalWeight] = useState(0);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({ fullName: '', phone: '', location: '' });

  const userId = localStorage.getItem('userId');

  useEffect(() => {
    if (!userId) {
      console.warn("No user ID found. Redirecting to login.");
      navigate('/login');
      return;
    }

    const fetchData = async () => {
      try {
        const profileRes = await fetch(`http://localhost:8082/api/profile/${userId}`);
        if (!profileRes.ok) throw new Error('Failed to fetch profile');

        const profileData = await profileRes.json();
        setUserData(profileData);
        setFormData({
          fullName: profileData.fullName,
          phone: profileData.phone,
          location: profileData.address,
        });

        const historyRes = await fetch(`http://localhost:8082/api/pickup/history/${userId}`);
        if (!historyRes.ok) throw new Error('Failed to fetch pickup history');

        const historyData = await historyRes.json();
        setPickupHistory(historyData);
        const total = historyData.reduce((sum, item) => sum + (parseFloat(item.weight) || 0), 0);
        setTotalWeight(total);

        setLoading(false);
      } catch (err) {
        console.error("Error fetching data:", err.message);
        alert("An error occurred while loading your data.");
        setLoading(false);
      }
    };

    fetchData();
  }, [userId, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:8082/api/profile/update/${userId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Profile updated successfully!');
      } else {
        alert('Failed to update profile');
      }
    } catch (err) {
      console.error("Error updating profile:", err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('userId');
    window.dispatchEvent(new Event('loginStatusChanged'));
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <p className="text-xl font-semibold">Loading your profile...</p>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-green-800 text-white p-6 space-y-6">
        <h2 className="text-2xl font-semibold">Dashboard</h2>
        <nav className="space-y-2">
          <button className="block text-left w-full hover:bg-green-700 p-2 rounded">Profile</button>
          <button className="block text-left w-full hover:bg-green-700 p-2 rounded">Pickup History</button>
          <button className="block text-left w-full hover:bg-green-700 p-2 rounded">Insights</button>
          <button className="block text-left w-full hover:bg-green-700 p-2 rounded">Terms & Conditions</button>
        </nav>
        <button
          onClick={handleLogout}
          className="mt-10 w-full bg-red-600 hover:bg-red-700 p-2 rounded text-white"
        >
          Logout
        </button>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-8 bg-gray-100 overflow-y-auto">
        <h1 className="text-3xl font-bold mb-6">Welcome, {userData?.fullName || 'User'}</h1>

        {/* Profile Update Section */}
        <section className="bg-white p-6 rounded shadow mb-8">
          <h2 className="text-xl font-semibold mb-4">Update Profile</h2>
          <form onSubmit={handleProfileUpdate} className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block mb-1 font-medium">Full Name</label>
              <input
                type="text"
                name="fullName"
                value={formData.fullName}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              />
            </div>
            <div>
              <label className="block mb-1 font-medium">Phone</label>
              <input
                type="text"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              />
            </div>
            <div>
              <label className="block mb-1 font-medium">Location</label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              />
            </div>
            <div>
              <label className="block mb-1 font-medium">Email (read-only)</label>
              <input
                type="email"
                value={userData?.email || ''}
                readOnly
                className="w-full p-2 border rounded bg-gray-100"
              />
            </div>
            <div>
              <label className="block mb-1 font-medium">Role (read-only)</label>
              <input
                type="text"
                value={userData?.role || ''}
                readOnly
                className="w-full p-2 border rounded bg-gray-100"
              />
            </div>
            <div className="col-span-full">
              <button type="submit" className="bg-green-700 text-white px-4 py-2 rounded hover:bg-green-800">
                Save Changes
              </button>
            </div>
          </form>
        </section>

        {/* Pickup History Section */}
        <section className="bg-white p-6 rounded shadow mb-8">
          <h2 className="text-xl font-semibold mb-4">Pickup History</h2>
          {pickupHistory.length === 0 ? (
            <p>No history available.</p>
          ) : (
            <table className="w-full table-auto">
              <thead>
                <tr className="bg-green-200">
                  <th className="p-2">Date</th>
                  <th className="p-2">Item Type</th>
                  <th className="p-2">Weight (kg)</th>
                  <th className="p-2">Status</th>
                </tr>
              </thead>
              <tbody>
                {pickupHistory.map((item, index) => (
                  <tr key={index} className="border-b">
                    <td className="p-2">{item.date}</td>
                    <td className="p-2">{item.itemType}</td>
                    <td className="p-2">{item.weight}</td>
                    <td className="p-2">{item.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </section>

        {/* Smart Insights Section */}
        <section className="bg-white p-6 rounded shadow mb-8">
          <h2 className="text-xl font-semibold mb-4">Smart Insights</h2>
          <p><strong>Total Recycled Weight:</strong> {totalWeight} kg</p>
          <p className="mt-2 text-sm text-gray-600">
            Keep recycling! You’re making the planet cleaner.
            More AI-based suggestions and insights are coming soon.
          </p>
        </section>

        {/* Terms and Conditions */}
        <section className="bg-white p-6 rounded shadow mb-8">
          <h2 className="text-xl font-semibold mb-4">Terms & Conditions</h2>
          <ul className="list-disc list-inside text-gray-700 space-y-2">
            <li>Only electronic waste is accepted.</li>
            <li>All pickups are subject to a ₹40 charge.</li>
            <li>Weight estimates must be accurate to avoid rejection.</li>
            <li>Pickups may be cancelled or rescheduled by the team if required.</li>
            <li>Items once picked up are not returnable.</li>
          </ul>
        </section>
      </main>
    </div>
  );
}

export default Profile;
