import React, { useState, useEffect, useRef } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '80vh',
};

const center = {
  lat: 20.5937,
  lng: 78.9629,
};

function Maps() {
  const [currentPosition, setCurrentPosition] = useState(null);
  const [places, setPlaces] = useState([]);
  const [selected, setSelected] = useState(null);
  const mapRef = useRef(null);

  // Fetch current location
  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      const userPos = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
      };
      setCurrentPosition(userPos);
    });
  }, []);

  // Perform Nearby Search when map is loaded
  const handleMapLoad = (map) => {
    mapRef.current = map;
    if (currentPosition) {
      const service = new window.google.maps.places.PlacesService(map);
      const request = {
        location: currentPosition,
        radius: 10000, // 10km
        keyword: 'kabadiwala OR scrap dealer OR e-waste collector',
      };

      service.nearbySearch(request, (results, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK) {
          const mappedPlaces = results.map((place, index) => ({
            id: index,
            name: place.name,
            position: {
              lat: place.geometry.location.lat(),
              lng: place.geometry.location.lng(),
            },
            address: place.vicinity,
          }));
          setPlaces(mappedPlaces);
        }
      });
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Nearest E-Waste Collection Centers</h2>
      <LoadScript googleMapsApiKey="AIzaSyAH8Mqz2Jazg0Q0tM_XA27CSgzRqS7v2Kk" libraries={['places']}>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={currentPosition || center}
          zoom={currentPosition ? 13 : 5}
          onLoad={handleMapLoad}
        >
          {/* User Location Marker */}
          {currentPosition && (
            <Marker
              position={currentPosition}
              icon={{ url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png' }}
            />
          )}

          {/* Place Markers */}
          {places.map((place) => (
            <Marker
              key={place.id}
              position={place.position}
              onClick={() => setSelected(place)}
            />
          ))}

          {/* Info Window */}
          {selected && (
            <InfoWindow
              position={selected.position}
              onCloseClick={() => setSelected(null)}
            >
              <div>
                <h3 className="font-semibold">{selected.name}</h3>
                <p>{selected.address}</p>
              </div>
            </InfoWindow>
          )}
        </GoogleMap>
      </LoadScript>

      {/* Optional: Text List of Results */}
      {places.length > 0 && (
        <div className="mt-4">
          <h3 className="text-xl font-semibold">Nearby Results:</h3>
          <ul className="list-disc ml-6 mt-2">
            {places.map((p) => (
              <li key={p.id}>
                <strong>{p.name}</strong> â€“ {p.address}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Maps;
