import React, { useState } from 'react';
import { useNavigate,Link } from 'react-router-dom';


function Login() {
  const [identifier, setIdentifier] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); 

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8082/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ identifier, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('isLoggedIn', 'true'); // ✅ Add this line
        localStorage.setItem('userId', data.userId);
        setMessage(data.message); // ✅ only show message like "Login successful"
        navigate("/profile");
        // localStorage.setItem('userId', data.userId);
      } else {
        setMessage(data.message); // show "User not found" or "Invalid password"
      }
    } catch (error) {
      setMessage('An error occurred. Try again later.');
      console.error('Login error:', error);
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold text-green-800 mb-4">Login</h2>
      <form onSubmit={handleLogin}>
        <div className="mb-4">
          <label className="block text-gray-700">Email / Phone Number</label>
          <input
            type="text"
            className="w-full p-2 border border-gray-300 rounded"
            value={identifier}
            onChange={(e) => setIdentifier(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Password</label>
          <input
            type="password"
            className="w-full p-2 border border-gray-300 rounded"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button
          type="submit"
          className="w-full bg-green-700 text-white p-2 rounded hover:bg-green-800"
        >
          Login
        </button>
      </form>
      {message && (
        <p className="mt-4 text-center text-sm text-red-600">{message}</p>
      )}
           <p className="mt-4 text-sm text-gray-600 text-center">
          Don’t have an account?{' '}
        <Link to="/register" className="text-green-700 hover:underline">
               Register here
            </Link>
</p>
    </div>
  );
}

export default Login;
