import React, { useEffect, useState } from 'react';

function Profile() {
  const [user, setUser] = useState({
    fullname: '',
    email: '',
    phone: '',
    location: '',
    role: '',
  });

  const [pickups, setPickups] = useState([]);
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    const userId = localStorage.getItem('userId');

    const fetchProfile = async () => {
      try {
        const response = await fetch(`http://localhost:8082/api/user/${userId}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    };

    const fetchPickups = async () => {
      try {
        const response = await fetch(`http://localhost:8082/api/pickups/${userId}`);
        const data = await response.json();
        // Defensive check: ensure it's an array
        setPickups(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error('Error fetching pickups:', error);
        setPickups([]); // fallback to empty array
      }
    };

    fetchProfile();
    fetchPickups();
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    try {
      const response = await fetch(`http://localhost:8082/api/user/update`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(user),
      });

      if (response.ok) {
        alert('Profile updated successfully!');
        setEditMode(false);
      } else {
        alert('Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  // Safely compute total recycled weight
  const totalWeight = Array.isArray(pickups)
    ? pickups.reduce((sum, p) => sum + (p.weight || 0), 0)
    : 0;

  return (
    <div className="max-w-5xl mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg space-y-10">
      {/* Profile Update Section */}
      <section>
        <h2 className="text-2xl font-bold text-green-800 mb-4">Profile Settings</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {['fullname', 'email', 'phone', 'location', 'role'].map((field) => (
            <div key={field}>
              <label className="block text-gray-600 capitalize">{field.replace('_', ' ')}</label>
              <input
                name={field}
                type={field === 'email' ? 'email' : 'text'}
                value={user[field] || ''}
                onChange={handleChange}
                readOnly={!editMode || field === 'email' || field === 'role'}
                className="w-full p-2 border border-gray-300 rounded bg-gray-100"
              />
            </div>
          ))}
        </div>
        <div className="mt-6 text-right">
          {editMode ? (
            <button onClick={handleUpdate} className="bg-green-700 text-white px-4 py-2 rounded hover:bg-green-800">
              Save Changes
            </button>
          ) : (
            <button onClick={() => setEditMode(true)} className="bg-green-700 text-white px-4 py-2 rounded hover:bg-green-800">
              Edit Profile
            </button>
          )}
        </div>
      </section>

      {/* Pickup History Section */}
      <section>
        <h2 className="text-2xl font-bold text-green-800 mb-4">Past Pickup Details</h2>
        {Array.isArray(pickups) && pickups.length > 0 ? (
          <table className="w-full table-auto border">
            <thead>
              <tr className="bg-green-100">
                <th className="p-2 border">Date</th>
                <th className="p-2 border">Item Type</th>
                <th className="p-2 border">Weight (kg)</th>
                <th className="p-2 border">Status</th>
              </tr>
            </thead>
            <tbody>
              {pickups.map((pickup, index) => (
                <tr key={index} className="text-center">
                  <td className="p-2 border">{pickup.date || 'N/A'}</td>
                  <td className="p-2 border">{pickup.itemType || 'N/A'}</td>
                  <td className="p-2 border">{pickup.weight || 'N/A'}</td>
                  <td className="p-2 border">{pickup.status || 'N/A'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-gray-500">No pickup history available. üöö</p>
        )}
      </section>

      {/* Smart Insights Section */}
      <section>
        <h2 className="text-2xl font-bold text-green-800 mb-4">E-Waste Insights & Tools</h2>
        <ul className="list-disc list-inside text-gray-700 space-y-2">
          <li>
            ‚ôªÔ∏è You have helped recycle <strong>{totalWeight}</strong> kg of e-waste. Great job!
          </li>
          <li>üìÖ Next ideal pickup date based on your pattern: <strong>Coming Soon</strong></li>
          <li>üìä View your recycling impact stats over time (chart module can be added later)</li>
          <li>üìå Save frequent pickup locations for faster booking (upcoming feature)</li>
        </ul>
      </section>
    </div>
  );
}

export default Profile;
