import React, { useState, useEffect, useRef } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '80vh',
};

const defaultCenter = {
  lat: 20.5937,
  lng: 78.9629,
};

function Maps() {
  const [currentPosition, setCurrentPosition] = useState(null);
  const [selected, setSelected] = useState(null);
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const mapRef = useRef(null);

  // Get user's current location
  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const userPos = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        };
        setCurrentPosition(userPos);
        setLoading(false);
      },
      (error) => {
        console.error("Geolocation Error:", error);
        setCurrentPosition(defaultCenter);
        setLoading(false);
      },
      {
        enableHighAccuracy: false,
        timeout: 10000,
        maximumAge: 60000,
      }
    );
  }, []);

  // Search for nearby kabadiwalas or e-waste collectors
  useEffect(() => {
    if (currentPosition && mapRef.current) {
      const service = new window.google.maps.places.PlacesService(mapRef.current);

      const request = {
        location: currentPosition,
        radius: 10000, // 10 km
        keyword: 'kabadiwala OR scrap dealer OR e-waste',
      };

      service.nearbySearch(request, (results, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK) {
          const found = results.map((place, index) => ({
            id: index,
            name: place.name,
            position: {
              lat: place.geometry.location.lat(),
              lng: place.geometry.location.lng(),
            },
            address: place.vicinity,
          }));
          setPlaces(found);
        }
      });
    }
  }, [currentPosition]);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Nearest E-Waste Collection Centers</h2>

      {loading ? (
        <div className="flex justify-center items-center h-64">
          <svg className="animate-spin h-10 w-10 text-green-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8H4z"></path>
          </svg>
          <p className="ml-4 text-gray-700 font-semibold">Detecting your location and finding nearby centers...</p>
        </div>
      ) : (
        <LoadScript googleMapsApiKey="AIzaSyAH8Mqz2Jazg0Q0tM_XA27CSgzRqS7v2Kk" libraries={['places']}>
          <GoogleMap
            mapContainerStyle={containerStyle}
            center={currentPosition}
            zoom={13}
            onLoad={(map) => {
              mapRef.current = map;
            }}
          >
            {/* User Location Marker */}
            <Marker
              position={currentPosition}
              icon={{ url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png' }}
            />

            {/* Nearby Places */}
            {places.map((place) => (
              <Marker
                key={place.id}
                position={place.position}
                onClick={() => setSelected(place)}
              />
            ))}

            {/* InfoWindow on Marker Click */}
            {selected && (
              <InfoWindow
                position={selected.position}
                onCloseClick={() => setSelected(null)}
              >
                <div>
                  <h3 className="font-semibold">{selected.name}</h3>
                  <p>{selected.address || "Address not available"}</p>
                  <p className="text-sm text-gray-500">Open 9am - 6pm</p>
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </LoadScript>
      )}
    </div>
  );
}

export default Maps;
